package javaclass;

public class Even5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int evenCount=0;
		for(int i=1;i<=20;i++)
		{
			if(i%2==0)
			{
				evenCount=evenCount+1;
				if(evenCount==5)
				{
					System.out.println(i);
				}
			}
				}
				//System.out.println(evenCount);
			}
		}
		
		

	


